    val listData: ArrayList<portofolio>
        get() {
            val list = arrayListOf<portofolio>()
            for (position in portofolioTitle.indices) {
                val mbr = portofolio()
                mbr.title = portofolioTitle[position]
                mbr.description = detail[position]
                mbr.photo = profile[position]
                mbr.ownerName = ownerName[position]
                mbr.email = email[position]
                list.add(mbr)
            }
            return list
        }
